import React from 'react';
import { Button, CircularProgress } from '@mui/material';

function ButtonComp(props) {
    const { children, isLoading, ...rest } = props;

    return (
      <Button {...rest}>
        {!isLoading && children}
        {isLoading && <CircularProgress color="secondary"/>}
      </Button>
    );
};

export default ButtonComp;
