import React, { useState } from 'react';
import { CloseRounded, Visibility, VisibilityOff } from "@mui/icons-material";


function TextInput({
    label,
    placeholder,
    name,
    value,
    error,
    handelChange,
    textArea,
    rows,
    columns,
    chipableInput,
    chipableArray,
    removeChip,
    height,
    small,
    popup,
    password
}) {
    const [showPassword, setShowPassword] = useState(false);
    return (
        <Container >
            <label small={small} popup={popup} error={error}>
                {label}
            </label>
            <OutlinedInput
                small={small}
                popup={popup}
                error={error}
                chipableInput={chipableInput}
                height={height}
            >
                {chipableInput ? (
                    <ChipWrapper>
                        {chipableArray.map((ship, index) => (
                            <Chip key={index}>
                                <span>{chip}</span>
                                <CloseRounded
                                    sx={{ fontSize: '14px' }}
                                    onClick={() => removeChip(name, index)}
                                />
                            </Chip>
                        ))}
                        <Input
                            placeholder={placeholder}
                            name={name}
                            value={value}
                            onChange={(e) => handelChange(e)}
                        />
                    </ChipWrapper>
                    ) : (
                        <>
                            <Input
                                popup={popup}
                                small={small}
                                as={textArea ? 'textarea' : 'input'}
                                name={name}
                                rows={rows}
                                columns={columns}
                                placeholder={placeholder}
                                value={value}
                                onChange={(e) => handelChange(e)}
                                type={password && !showPassword ? 'password' : 'text'}
                            />
                            {password && (
                                <>
                                    {showPassword ? (
                                        <>
                                            <Visibility onClick={() => setShowPassword(false)} />
                                        </>
                                    ) : (
                                        <>
                                            <VisibilityOff onClick={() => setShowPassword(true)} />
                                        </>
                                    )}
                                </>
                            )}
                        </>
                    )}

            </OutlinedInput>
            {error && (
                <Error small={small} popup={popup}>
                    {error}
                </Error>
            )}
        </Container>
    )
}

export default TextInput;
