import react, { useState } from 'react';
import styles from '../header/header.module.css';
import logo from '../../assets/logo2.jpg';
import logo_name from '../../assets/logo_name.jpg';
import { NavLink } from 'react-router-dom';
import SearchBar from '../search/search';
import AccountMenu from '../accountMenu/accountMenu';
import Cart from '../cart/cardCard';
import ReactDOM from 'react-dom';

// Font awesome icons
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartShopping, faUser, faBars } from '@fortawesome/free-solid-svg-icons';
// import { faCartShopping, faUser, faBars } from '@fortawesome/fontawesome-svg-core';

// temp icons
// import burgermenu from '../../assets/list-solid.jpg';
// import cart from '../../assets/cart-shopping-solid.jpg';
// import account from '../../assets/account.jpg';



function Header() {
    const [isOpenCat, setIsOpenCat] = useState(false);
    const [isOpenAcc, setIsOpenAcc] = useState(false);
    const [isOpenCart, setIsOpenCart] = useState(false);

    const displayMenu = () => {
        if (isOpenAcc) {
            setIsOpenAcc(!isOpenAcc)
        };
        if (isOpenCart) {
            setIsOpenCart(!isOpenCart)
        };
        setIsOpenCat(!isOpenCat)
    };

    const displayAccount = () => {
        if (isOpenCat) {
            setIsOpenCat(!isOpenCat)
        };
        if (isOpenCart) {
            setIsOpenCart(!isOpenCart)
        };
        setIsOpenAcc(!isOpenAcc)
    };

    const displayCart = () => {
        if (isOpenAcc) {
            setIsOpenAcc(!isOpenAcc)
        };
        if (isOpenCat) {
            setIsOpenCat(!isOpenCat)
        };
        setIsOpenCart(!isOpenCart)
    };



    const openCat = {
        transition: 'all 0.6s ease-in-out',
        transform: isOpenCat ? "translateY(0)" : "translateY(-200%)",
        position: 'absolute',
        zIndex: isOpenCat ? 1000 : -1000
    };

    const openAcc = {
        transition: 'all 0.6s ease-in-out',
        transform: isOpenAcc ? "translateY(0)" : "translateY(-200%)",
        position: 'absolute',
        zIndex: isOpenAcc ? 1000 : -1000
    };

    const openCart = {
        transition: 'all 0.6s ease-in-out',
        transform: isOpenCart ? "translateY(0)" : "translateY(-200%)",
        position: 'absolute',
        zIndex: isOpenCart ? 1000 : -1000
    };


    return (
        <div>
            <div className={styles.navbar}>
                <NavLink to='/'><img src={logo_name} alt='E-Commerce Quick logo' /></NavLink>
                <SearchBar className={styles.search} />
                <FontAwesomeIcon icon={faCartShopping} />
                <FontAwesomeIcon icon={faBars} />
                <FontAwesomeIcon icon={faUser} />

            </div>
            <div style={openCat}>
                <ul>
                    <li>Categories</li>
                    <li>t-shirt</li>
                    <li>shoes</li>
                    <li>other thing cannot rememeber</li>
                </ul>
            </div>
            <div style={openAcc}>
                <AccountMenu />
            </div>
            <div style={openCart}>
                <Cart />
            </div>
        </div>
    );
};

export default Header;
